pub struct Solution;

impl Solution {
    pub fn find_the_difference(s: String, t: String) -> char {
        let mut vec_s: Vec<char> = s.chars().collect();
        let mut vec_t: Vec<char> = t.chars().collect();

        vec_s.sort();
        vec_t.sort();
        
        for (from_s, from_t) in vec_s.iter().zip(vec_t.iter()) {
            if from_s != from_t {
                return *from_t;
            }
        }
        *vec_t.last().unwrap()
    }
}


#[cfg(test)]
mod tests{
    use super::*;

    #[test]
    fn test1() {
        assert_eq!('e', Solution::find_the_difference("abcd".to_string(), "abcde".to_string()));
        assert_eq!('e', Solution::find_the_difference("abcd".to_string(), "eabcd".to_string()));
        assert_eq!('e', Solution::find_the_difference("abcd".to_string(), "abecd".to_string()));
        assert_eq!('a', Solution::find_the_difference("abcd".to_string(), "abacd".to_string()));
    }


    #[test]
    fn test2() {
        let s = "".to_string();
        let t = "".to_string();
        assert_eq!('t', Solution::find_the_difference(s, t));
    }
}